import React from 'react';

const initalState = {
  fullName: '',
  email: '',
  CPF: '',
};

class PersonalInformation extends React.Component {
  constructor(props) {
    super(props);
    this.state = { initalState }

    this.handleChange = this.handleChange.bind(this);
  };

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value })
  }

  render(){
    const { name } = this.state;
    return (
      <form>
        <p>Nome completo:*</p>
        <input 
          type="Text"
          maxLength="40"
          value={ name }
          name="fullName"
          required
          onChange={ this.handleChange }
        />
        <p>E-mail:*</p>
        <input
          type="email"
          maxLength="50"
          name="email"
          required
          onChange={ this.handleChange }
        />
        <p>CPF:</p>
        <input
          type="text"
          maxLength="11"
          name="CPF"
          required
          onChange={ this.handleChange }
        />
        <p>Endere√ßo:</p>
        <input 
          type="test"
          maxLength="200"
          required
        />
      </form>
    )
  }
}

export default PersonalInformation;
