import React from 'react';

class PersonalInformation extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
    }
  };

  render(){
    const { name } = this.state;
    return (
      <form>
        <p>Nome:</p>
        <input type="Text" maxLength="40" value={ name } required />
      </form>
    )
  }
}

export default PersonalInformation;
