import React from 'react';

const initalState = {
  fullName: '',
  email: '',
  CPF: '',
  address: '',
  city: '',
  state: '',
};

class PersonalInformation extends React.Component {
  constructor(props) {
    super(props);
    this.state = { initalState }

    this.handleChange = this.handleChange.bind(this);
  };

  handleChange({ target }) {
    let { name, value } = target;

    if (name === 'fullName') value = value.toUpperCase();

    this.setState({ [name]: value })
  }

  render(){
    const { name } = this.state;
    return (
      <form>
        <p>Nome completo:*</p>
        <input 
          type="Text"
          maxLength="40"
          value={ name }
          name="fullName"
          required
          onChange={ this.handleChange }
        />
        <p>E-mail:*</p>
        <input
          type="email"
          maxLength="50"
          name="email"
          required
          onChange={ this.handleChange }
        />
        <p>CPF:</p>
        <input
          type="text"
          maxLength="11"
          name="CPF"
          required
          onChange={ this.handleChange }
        />
        <div className="adress">
          <p>Rua e n√∫mero:</p>
          <input 
            type="text"
            maxLength="200"
            required
            name="address"
          />
          <p>Cidade:</p>
          <input 
            type="text"
            maxLength="28"
            required
            name="city"
          />
          <p>Estado:</p>
          <select name="state">

          </select>
        </div>
      </form>
    )
  }
}

export default PersonalInformation;
